import { Component } from '@angular/core';

@Component({
  selector: 'app-typewriter-sound',
  templateUrl: './typewriter-sound.component.html',
  styleUrls: ['./typewriter-sound.component.scss']
})
export class TypewriterSoundComponent {
  playTypingSound(): void {
    const typingSound = new Audio();
    typingSound.src = 'https://example.com/daktilo_sesi.mp3';
    typingSound.load();
    typingSound.play();
  }

}
<textarea id="codeInput" placeholder="Kodunuzu buraya yazın..." (keydown)="playTypingSound()"></textarea>
<audio id="typingSound" src="https://example.com/daktilo_sesi.mp3"></audio>
